import { useState } from "react";
import classes from "./AuthForm.module.css";

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const firstNameChangeHandler = (event) => {
    setFirstName(event.target.value);
  };
  const lastNameChangeHandler = (event) => {
    setLastName(event.target.value);
  };
  const emailChangeHandler = (event) => {
    setEmail(event.target.value);
  };
  const passwordChangeHandler = (event) => {
    setPassword(event.target.value);
  };
  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setFirstName("");
    setLastName("");
    setEmail("");
    setPassword("");
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? "LOGIN" : "CREATE ACCOUNT"}</h1>
      <form onSubmit={formSubmissionHandler}>
        {!isLogin && (
          <div className={classes.control}>
            <label htmlFor="fname">FIRST NAME</label>
            <input
              id="fname"
              type="text"
              required
              value={firstName}
              onChange={firstNameChangeHandler}
            />
          </div>
        )}
        {!isLogin && (
          <div className={classes.control}>
            <label htmlFor="lname">LAST NAME</label>
            <input
              id="lname"
              type="text"
              required
              onChange={lastNameChangeHandler}
              value={lastName}
            />
          </div>
        )}
        <div className={classes.control}>
          <label htmlFor="email">EMAIL</label>
          <input id="email" type="email" required onChange={emailChangeHandler} value={email}/>
        </div>
        <div className={classes.control}>
          <label htmlFor="password">PASSWORD</label>
          <input id="password" type="password" required onChange={passwordChangeHandler} />
        </div>
        <div>
          <button className={classes.action} type="submit">
            {isLogin ? "SIGN IN" : "CREATE"}
          </button>
          {isLogin && (
            <p onClick={switchAuthModeHandler} className={classes.switchBtn}>
              Create account
            </p>
          )}
        </div>
      </form>
    </section>
  );
};
export default AuthForm;
