import Card from "../UI/Card";
import CartItem from "./CartItem";

import classes from "./Cart.module.css";

const Cart = (props) => {
  const cartItems = [
    { id: "c1", name: "Sushi", price: "22.99", amount: "1" },
    { id: "c2", name: "Schnitzel", price: "16.5", amount: "4" },
  ];
  return (
    <Card className={classes.cart}>
      <ul>
        {cartItems.map((item) => (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
          />
        ))}
      </ul>
      <div className={classes.price}>
        <h2>Total Amount</h2>
        <h3>$88.99</h3>
      </div>
      <div>
        <button></button>
      </div>
    </Card>
  );
};
export default Cart;
