import Card from "../UI/Card";
import CartItem from "./CartItem";

import classes from "./Cart.module.css";
import Modal from "../UI/Modal";


const Cart = (props) => {
  
  const cartItems = [
    { id: "c1", name: "Sushi", price: "22.99", amount: "1" },
    { id: "c2", name: "Schnitzel", price: "16.50  ", amount: "4" },
  ];
  const closeCartHandler = (props) => {
    props.onCloseCart()
  };
  return (
    <Modal>
      <ul className={classes.cart}>
        {cartItems.map((item) => (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
          />
        ))}
      </ul>
      <div className={classes.price}>
        <h2>Total Amount</h2>
        <h3>$88.99</h3>
      </div>
      <div className={classes.action}>
        <button className={classes.close} onClick={closeCartHandler}>
          Close
        </button>
        <button className={classes.order}>Order</button>
      </div>
    </Modal>
  );
};
export default Cart;
